import React, { useState } from 'react';
import { db } from '../services/firebaseConfig';
import { collection, addDoc } from 'firebase/firestore';

const SurveyBuilder = () => {
  const [questions, setQuestions] = useState([]);
  const [validationError, setValidationError] = useState(null);
  const [surveyTitle, setSurveyTitle] = useState('');

  // Question type interfaces
  const addMultipleChoiceQuestion = () => {
    const newQuestion = {
      type: 'multiple-choice',
      text: '',
      options: ['', ''],
      required: false
    };
    setQuestions([...questions, newQuestion]);
  };

  const addRatingScaleQuestion = () => {
    const newQuestion = {
      type: 'rating-scale',
      text: '',
      min: 1,
      max: 5,
      required: false
    };
    setQuestions([...questions, newQuestion]);
  };

  const addConditionalQuestion = () => {
    const newQuestion = {
      type: 'conditional',
      text: '',
      condition: null,
      dependsOn: null,
      required: false
    };
    setQuestions([...questions, newQuestion]);
  };

  const updateQuestion = (index, updates) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index] = { ...updatedQuestions[index], ...updates };
    setQuestions(updatedQuestions);
  };

  const validateSurvey = () => {
    // Basic validation
    if (!surveyTitle.trim()) {
      setValidationError('Survey must have a title');
      return false;
    }

    const requiredQuestionsMissing = questions.some(
      q => q.required && (!q.text || q.text.trim() === '')
    );

    if (requiredQuestionsMissing) {
      setValidationError('Please fill in all required questions');
      return false;
    }

    setValidationError(null);
    return true;
  };

  const submitSurvey = async () => {
    if (!validateSurvey()) return;

    try {
      const surveyRef = collection(db, 'surveys');
      await addDoc(surveyRef, {
        title: surveyTitle,
        questions,
        createdAt: new Date()
      });

      // Reset form after successful submission
      setQuestions([]);
      setSurveyTitle('');
      setValidationError(null);
      alert('Survey created successfully!');
    } catch (error) {
      console.error('Error submitting survey:', error);
      setValidationError('Failed to submit survey');
    }
  };

  return (
    <div className="survey-builder">
      <h2>Create New Survey</h2>

      {/* Survey Title Input */}
      <input
        type="text"
        placeholder="Enter Survey Title"
        value={surveyTitle}
        onChange={(e) => setSurveyTitle(e.target.value)}
        className="survey-title-input"
      />

      {/* Question Type Buttons */}
      <div className="question-type-buttons">
        <button 
          data-testid="add-multiple-choice"
          onClick={addMultipleChoiceQuestion}
        >
          Add Multiple Choice
        </button>
        <button 
          data-testid="add-rating-scale"
          onClick={addRatingScaleQuestion}
        >
          Add Rating Scale
        </button>
        <button 
          data-testid="add-conditional-question"
          onClick={addConditionalQuestion}
        >
          Add Conditional Question
        </button>
      </div>

      {/* Questions List */}
      {questions.map((question, index) => (
        <div key={index} className="question-item">
          {question.type === 'multiple-choice' && (
            <div>
              <input
                data-testid="question-text-input"
                placeholder="Enter question"
                value={question.text}
                onChange={(e) => updateQuestion(index, { text: e.target.value })}
              />
              {question.options.map((option, optIndex) => (
                <input
                  key={optIndex}
                  data-testid={`option-${optIndex + 1}-input`}
                  placeholder={`Option ${optIndex + 1}`}
                  value={option}
                  onChange={(e) => {
                    const newOptions = [...question.options];
                    newOptions[optIndex] = e.target.value;
                    updateQuestion(index, { options: newOptions });
                  }}
                />
              ))}
              <button 
                onClick={() => {
                  const newOptions = [...question.options, ''];
                  updateQuestion(index, { options: newOptions });
                }}
              >
                Add Option
              </button>
              <label>
                <input
                  data-testid="required-question-checkbox"
                  type="checkbox"
                  checked={question.required}
                  onChange={(e) => updateQuestion(index, { required: e.target.checked })}
                />
                Required Question
              </label>
            </div>
          )}

          {question.type === 'rating-scale' && (
            <div>
              <input
                data-testid="rating-question-text"
                placeholder="Enter rating question"
                value={question.text}
                onChange={(e) => updateQuestion(index, { text: e.target.value })}
              />
              <div data-testid="rating-scale">
                Rating Scale Range
                <input
                  type="number"
                  min="1"
                  max="10"
                  value={question.max}
                  onChange={(e) => updateQuestion(index, { max: parseInt(e.target.value) })}
                />
              </div>
              <label>
                <input
                  type="checkbox"
                  checked={question.required}
                  onChange={(e) => updateQuestion(index, { required: e.target.checked })}
                />
                Required Question
              </label>
            </div>
          )}

          {question.type === 'conditional' && (
            <div>
              <input
                data-testid="conditional-question-text"
                placeholder="Enter conditional question"
                value={question.text}
                onChange={(e) => updateQuestion(index, { text: e.target.value })}
              />
              <select
                data-testid="condition-selector"
                value={question.condition || ''}
                onChange={(e) => updateQuestion(index, { condition: e.target.value })}
              >
                <option value="">Select Condition</option>
                {questions
                  .filter(q => q.type === 'multiple-choice')
                  .map((q, idx) => (
                    <optgroup key={idx} label={q.text}>
                      {q.options.map((option, optIdx) => (
                        <option key={optIdx} value={option}>
                          {option}
                        </option>
                      ))}
                    </optgroup>
                  ))
                }
              </select>
              <label>
                <input
                  type="checkbox"
                  checked={question.required}
                  onChange={(e) => updateQuestion(index, { required: e.target.checked })}
                />
                Required Question
              </label>
            </div>
          )}

          {/* Remove Question Button */}
          <button 
            onClick={() => {
              const updatedQuestions = questions.filter((_, qIndex) => qIndex !== index);
              setQuestions(updatedQuestions);
            }}
          >
            Remove Question
          </button>
        </div>
      ))}

      {/* Validation Error Display */}
      {validationError && (
        <div data-testid="validation-error" className="error-message">
          {validationError}
        </div>
      )}

      {/* Submit Button */}
      <button 
        data-testid="submit-survey"
        onClick={submitSurvey}
        disabled={questions.length === 0}
      >
        Create Survey
      </button>
    </div>
  );
};

export default SurveyBuilder;
