import React, { useState } from "react";
import { signInWithGoogle, signInWithEmail } from "./firebase-config";
import { motion } from "framer-motion";

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // State for error message

  const handleEmailLogin = async () => {
    setError(""); // Reset error before trying to log in

    if (!email || !password) {
      setError("Please enter both email and password.");
      return;
    }

    const response = await signInWithEmail(email, password);
    if (!response.success) {
      setError(response.error); // Show detailed error message
    }
  };

  return (
    <div style={containerStyle}>
      <motion.div 
        initial={{ opacity: 0, y: -20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ duration: 0.6 }}
        style={cardStyle}
      >
        <h1 style={{ color: "#333" }}>Welcome to Our App</h1>
        <p>Please sign in to continue.</p>

        {/* Display error message */}
        {error && <motion.p style={errorStyle} initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          {error}
        </motion.p>}

        {/* Email & Password Login */}
        <div style={inputContainerStyle}>
          <motion.input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
            whileFocus={{ scale: 1.05 }}
          />
          <motion.input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
            whileFocus={{ scale: 1.05 }}
          />
          <motion.button 
            onClick={handleEmailLogin} 
            style={buttonStyle} 
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Sign in with Email
          </motion.button>
        </div>

        <p style={{ color: "#666", fontSize: "14px" }}>or</p>

        {/* Google Login */}
        <motion.button 
          onClick={signInWithGoogle} 
          style={googleButtonStyle} 
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Sign in with Google
        </motion.button>
      </motion.div>
    </div>
  );
}

// Styles
const containerStyle = {
  height: "100vh",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  background: "linear-gradient(135deg, #ff9a9e, #fad0c4, #fad0c4)",
};

const cardStyle = {
  background: "white",
  padding: "40px",
  borderRadius: "10px",
  boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
  textAlign: "center",
  width: "350px"
};

const errorStyle = {
  color: "red",
  fontSize: "14px",
  marginBottom: "10px"
};

const inputContainerStyle = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  gap: "10px",
  marginBottom: "20px"
};

const inputStyle = {
  padding: "12px",
  width: "90%",
  fontSize: "16px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  transition: "all 0.3s ease-in-out",
  outline: "none"
};

const buttonStyle = {
  padding: "12px 20px",
  fontSize: "16px",
  cursor: "pointer",
  backgroundColor: "#4CAF50",
  color: "white",
  border: "none",
  borderRadius: "5px",
  transition: "all 0.3s ease",
};

const googleButtonStyle = {
  padding: "12px 20px",
  fontSize: "16px",
  cursor: "pointer",
  backgroundColor: "#4285F4",
  color: "white",
  border: "none",
  borderRadius: "5px",
  transition: "all 0.3s ease",
};

export default LoginPage;
